import React, { useState } from 'react'
import { NavLink } from "react-router-dom"
import { Registrar } from "./Registrar"
import { Listar } from "./Listar"
import { Estadistica } from "./Estadistica"
import Logo from '../assets/img/geeksmart.jpg';

export const Menu = (props) => {

  const [reg, setReg] = useState("");
  const [lis, setLis] = useState("");
  const [est, setEst] = useState("");
 
  

  function cerrarSesion(){
    document.getElementById("caja_menu").style.display = "none";
    document.getElementById("form_login").style.display = "block";
    document.getElementById("txtusu").value = "";
    document.getElementById("txtpas").value = "";
    document.getElementById("txtusu").focus();
  }


  function op_registrar(){
    setReg("1");
    setLis("0");
    setEst("0");
  }


  function op_listar(){
    setReg("0");
    setLis("1");
    setEst("0");
  }


  function op_estadistica(){
    setReg("0");
    setLis("0");
    setEst("1");
  }



  return (
    
    
    <>
       <div class="flex  overflow-hidden bg-white rounded-lg">
    <div class="hidden md:flex md:flex-shrink-0" >
        <div class="flex flex-col w-64" >
            <div class="flex flex-col flex-grow pt-5 overflow-y-auto bg-indigo-700 border-r">
                <div class="flex flex-col items-center flex-shrink-0 px-4">
                    <a class="px-8 text-left focus:outline-none" href="#">
                        <h1 class="block p-2 text-xl font-medium tracking-tighter text-white transition duration-500 ease-in-out transform cursor-pointer hover:text-white">
                        <div class="ml-3">
                                <p class="text-lg font-medium text-white">Bienvenido  { props.usu.toUpperCase() }</p>
                            </div>
                          
                           </h1>
                    </a>
