import React, { useState } from 'react'
import { NavLink } from "react-router-dom"
import { Registrar } from "./Registrar"
import { Listar } from "./Listar"
import { Estadistica } from "./Estadistica"
import Logo from '../assets/img/geeksmart.jpg';
import { Sidebar } from "./Sidebar"

export const Menu = (props) => {

  const [reg, setReg] = useState("");
  const [lis, setLis] = useState("");
  const [est, setEst] = useState("");
  const [sid, setSide] = useState("");
  

  function cerrarSesion(){
    document.getElementById("caja_menu").style.display = "none";
    document.getElementById("form_login").style.display = "block";
    document.getElementById("txtusu").value = "";
    document.getElementById("txtpas").value = "";
    document.getElementById("txtusu").focus();
  }


  function op_registrar(){
    setReg("1");
    setLis("0");
    setEst("0");
    setSide("0");
  }


  function op_listar(){
    setReg("0");
    setLis("1");
    setEst("0");
    setSide("0");
  }


  function op_estadistica(){
    setReg("0");
    setLis("0");
    setEst("1");
    setSide("0");
  }

  function op_sidebar(){
    setReg("0");
    setLis("0");
    setEst("0");
    setSide("1");
  }

  return (
    
    
    <>



<NavLink to="" className=""  onClick={ op_registrar } > <span className="ml-4">Precio</span></NavLink>
      
<NavLink to="" className=""   onClick={ op_registrar } > <span className="ml-4">Registrar</span></NavLink>  

<NavLink to="" className=""    onClick={ op_estadistica } > <span className="ml-4">Dashboard</span></NavLink>


<NavLink to="" className=""   onClick={ op_listar } > <span className="ml-4">Clientes</span></NavLink>
<p className="">Bienvenido  { props.usu.toUpperCase() }</p>
<a className="" white=""  href=" "  onClick={ cerrarSesion } >



                                   
                                   <span className="ml-4">Cerrar Seccion</span>


                                   


                               </a>
      
                               <img className="h-10 w-10 rounded-full mx-auto h-23 w-auto space-y-6" src={Logo}></img>
      
                               <main className=" container mx-auto  overflow-y-auto focus:outline-none">
        <h1 className="text-lg text-neutral-600"> { reg === "1" && <Registrar/> }
  { lis === "1" && <Listar/>}
   <Estadistica/> </h1>


   <Sidebar/>
          
        </main>

       
    
    
        
    

  

       
 

</>
  



  )
}
