import React, { useState } from 'react'
import { NavLink } from "react-router-dom"
import { Registrar } from "./Registrar"
import { Listar } from "./Listar"
import { Estadistica } from "./Estadistica"
import Logo from '../assets/img/geeksmart.jpg';

export const Menu = (props) => {

  const [reg, setReg] = useState("");
  const [lis, setLis] = useState("");
  const [est, setEst] = useState("");
 
  

  function cerrarSesion(){
    document.getElementById("caja_menu").style.display = "none";
    document.getElementById("form_login").style.display = "block";
    document.getElementById("txtusu").value = "";
    document.getElementById("txtpas").value = "";
    document.getElementById("txtusu").focus();
  }


  function op_registrar(){
    setReg("1");
    setLis("0");
    setEst("0");
  }


  function op_listar(){
    setReg("0");
    setLis("1");
    setEst("0");
  }


  function op_estadistica(){
    setReg("0");
    setLis("0");
    setEst("1");
  }



  return (
    
    
    <>
     <nav>
                        <ul>
                          
                            


                            <li>
                                <a  href="#"> <NavLink    onClick={ op_registrar } > <span >Registrar</span></NavLink>


                                          </a>
                                      </li>




                            <li>
                                <a  href="#"> <NavLink    onClick={ op_estadistica } > <span >Dashboard</span></NavLink>


                                          </a>
                                      </li>




                            <li>

                       

                      <a  href="#"> <NavLink    o onClick={ op_registrar } > <span >Precio</span></NavLink>
    
   
                              </a>
                          </li>

                   





                            <li>


                                <a href="#"> <NavLink  onClick={ op_listar } > <span >Clientes</span></NavLink>
                                    
                                   
                                </a>
                            </li>
                           
                        </ul>
                        <p className=" ">Servicio</p>
                        <ul>
                            <li>
                               
                            </li>
                           

                           



                            <li>
                               
                                    
                                    <span className="">configuracion</span>
                            </li>
                            <li>
                                <a  white=""  href=" "  onClick={ cerrarSesion } >
                                   
                                    <span className="">Cerrar Seccion</span>


                                    


                                </a>
                            </li>




                            
                        </ul>
                    </nav>

    
        
        <h1 > { reg === "1" && <Registrar/> }
  { lis === "1" && <Listar/>}
  { est === "1" && <Estadistica/> }</h1>
            <div >

           
               
                
            </div>
        
   

  

       
 

</>
  



  )
}
