import React, { useState } from 'react'
import { NavLink } from "react-router-dom"
import { Registrar } from "./Registrar"
import { Listar } from "./Listar"
import { Estadistica } from "./Estadistica"
import Logo from '../assets/img/geeksmart.jpg';

export const Menu = (props) => {

  const [reg, setReg] = useState("");
  const [lis, setLis] = useState("");
  const [est, setEst] = useState("");
 
  

  function cerrarSesion(){
    document.getElementById("caja_menu").style.display = "none";
    document.getElementById("form_login").style.display = "block";
    document.getElementById("txtusu").value = "";
    document.getElementById("txtpas").value = "";
    document.getElementById("txtusu").focus();
  }


  function op_registrar(){
    setReg("1");
    setLis("0");
    setEst("0");
  }


  function op_listar(){
    setReg("0");
    setLis("1");
    setEst("0");
  }


  function op_estadistica(){
    setReg("0");
    setLis("0");
    setEst("1");
  }



  return (
    
    
    <>
       <div class="flex  overflow-hidden bg-white rounded-lg">
    <div class="absolute bg-gray-800 text-white w-49 min-h-screen overflow-y-auto transition-transform transform -translate-x-full h-2 ease-in-out duration-300"
            id="sidebar" >
        <div class="flex flex-col w-64" >
            <div class="flex flex-col flex-grow pt-5 overflow-y-auto bg-indigo-700 border-r">
                <div class="flex flex-col items-center flex-shrink-0 px-4">
                   
                        
                        <div class="ml-3">
                                <p class="text-lg font-medium text-white">Bienvenido  { props.usu.toUpperCase() }</p>
                            </div>
                          
                           
                    
               
                </div>
                <div class="flex flex-col flex-grow px-4 mt-5">
                    <nav class="flex-1 space-y-1 bg-indigo-700">
                        <ul>
                          
                            


                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" href="#"> <NavLink to="" className=""   onClick={ op_registrar } > <span class="ml-4">Registrar</span></NavLink>


                                          </a>
                                      </li>




                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" href="#"> <NavLink to="" className=""   onClick={ op_estadistica } > <span class="ml-4">Dashboard</span></NavLink>


                                          </a>
                                      </li>




                            <li>

                       

                      <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" href="#"> <NavLink to="" className=""  o onClick={ op_registrar } > <span class="ml-4">Precio</span></NavLink>
    
   
                              </a>
                          </li>

                   





                            <li>


                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" href="#"> <NavLink to="" className=""  onClick={ op_listar } > <span class="ml-4">Clientes</span></NavLink>
                                    
                                   
                                </a>
                            </li>
                           
                        </ul>
                        <p class="px-4 pt-2  text-white uppercase">Servicio</p>
                        <ul>
                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" white=""  href="#">
                                   
                                    <span class="ml-4"> telefono</span>
                                </a>
                            </li>
                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" white="" href="#">
                                   
                                    <span class="ml-4"> pc</span>
                                </a>
                            </li>

                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" white="" href="#">
                                   
                                    <span class="ml-4"> otros</span>
                                </a>
                            </li>



                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" href="#">
                                    
                                    <span class="ml-4">configuracion</span></a>
                            </li>
                            <li>
                                <a class="inline-flex items-center w-full px-4 py-2 mt-1 text-base text-white transition duration-500 ease-in-out transform border-indigo-800 rounded-lg hover:border-indigo-800 focus:shadow-outline hover:bg-indigo-600" white=""  href=" "  onClick={ cerrarSesion } >
                                   
                                    <span class="ml-4">Cerrar Seccion</span>


                                    


                                </a>
                            </li>




                            
                        </ul>
