import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import { Registrar } from './Registrar';
import { Listar } from './Listar';
import { Estadistica } from './Estadistica';
import Logo from '../assets/img/geeksmart.jpg';
import { Button, Flex } from 'antd';

const menuItems = [
  { key: '1', label: 'Dashboard', onClick: () => setMenu('estadistica') },
  { key: '2', label: 'Clientes', onClick: () => setMenu('listar') },
  { key: '3', label: 'Registrar', onClick: () => setMenu('registrar') },
  { key: '4', label: 'Calendar' },
  { key: '5', label: 'Cerrar Sesión', onClick: cerrarSesion },
];

export const Menu = () => {
  const [menu, setMenu] = useState('');
  const formLoginRef = useRef(null);
  const cajaMenuRef = useRef(null);

  const cerrarSesion = () => {
    formLoginRef.current.style.display = 'block';
    cajaMenuRef.current.style.display = 'none';
    // Reset form values
  };

  return (
    <div className="min-h-full">
      <nav className="bg-gray-800">
        {/* Menu items */}
        {menuItems.map((menuItem) => (
          <a
            key={menuItem.key}
            href="#"
            className="text-gray-300 hover:bg-gray-700 hover:text-white rounded-md px-3 py-2 text-sm font-medium"
            onClick={menuItem.onClick}
          >
            <NavLink to="" className="" onClick={menuItem.onClick}>
              {menuItem.label}
            </NavLink>
          </a>
        ))}
      </nav>

      {/* Mobile menu */}
      <div className="md:hidden" id="mobile-menu">
        {/* Mobile menu items */}
        {menuItems.map((menuItem) => (
          <a
            key={menuItem.key}
            href="#"
            className="bg-gray-900 text-white block rounded-md px-3 py-2 text-base font-medium"
            onClick={menuItem.onClick}
          >
            <NavLink to="" className="" onClick={menuItem.onClick}>
              {menuItem.label}
            </NavLink>
          </a>
        ))}
      </div>

      {/* Content */}
      {menu === 'estadistica' && <Estadistica />}
      {menu === 'listar' && <Listar />}
      {menu === 'registrar' && <Registrar />}
    </div>
  );
};
